package com.techgirl.SpringKafka.service.producer;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Value;import org.springframework.kafka.core.KafkaTemplate;import org.springframework.stereotype.Service;@Servicepublic class KafkaStringProducer {    private final KafkaTemplate<String, String> kafkaTemplate;    @Value("${kafka-topic}")    private String topicName;    @Autowired    public KafkaStringProducer(KafkaTemplate<String, String> kafkaTemplate) {        this.kafkaTemplate = kafkaTemplate;    }    public void produceMessage(String message) {        kafkaTemplate.send(topicName, message);        System.out.println("Message sent to Kafka: " + message);    }}